import React, { useState } from "react";
import './App.css';
import fs from "fs/promises"

function App() {
  const [values, setValues] = useState()

  const handleChangeValues = value => {
    setValues(preValue => ({
      ...preValue, 
      [value.target.name]: value.target.value
    }))
  }

  const handleClickButton = () => {
    console.log(values)
  }

  const searchClickBtn = async () =>{
     const product = await fs.readFile(`../database/results.json`)
     console.log(product)
  }

  return (
    <div className="app--container">
      <div className= "register--container">
          <h1>Validacao de produto</h1>
          <label className="product-code--label">codigo do produto:</label>
          <input name="codigo" className="product-code--input"onChange={handleChangeValues}></input>
          <button className="search--btn" onClick={searchClickBtn}>Procurar</button>
          <label className="product-name--label">nome do produto:</label>
          <p name="nome" className="product-name--input"></p>
          <label className="product-first-value--label">valor atual do produto:</label>
          <p className="product-first-value--p">R$ 0000,00</p>
          <label className="product-second-value--label">novo valor do produto:</label>
          <input name="valor novo" className="product-second-value--input"onChange={handleChangeValues}></input>
          <button className="validation--btn" onClick={handleClickButton}>Validar</button>
      </div>

    </div>
  );
}

export default App;
